import {useState} from 'react'
import AddItem from './additem';

const Showtable=(props)=>{
    //האם לחצו על עריכה
  const [isedit,setisedit]  =useState(false)
  //המוצר שאותו נירצה לערןך
  const [itemToedit,setitemToedit]=useState({})
  const edit=(item)=>{
    setisedit(!isedit)
    setitemToedit(item)
  }
return <div className="container">


 <table className="table">
    <tr>
        <th>קוד</th>
        <th>שם</th>
        <th>מחיר</th>
        <th>תמונה</th>
    </tr>
   {
       props.mylist.map(x=>(
           <tr>
               <td>{x.id}</td>
               <td>{x.name}</td>
               <td>{x.price}</td>
               <td><img src={x.img} width="50" height='50'></img></td>
               {/* props.ondelete(x.id)-קריאה  לפונקציה מהאבא */}
               <td><button onClick={()=>props.ondelete(x.id)}>מחק</button></td>
               <td><button onClick={()=>edit(x)}>ערוך</button></td>
           </tr>
       ))
   }
   
</table>
{isedit && <AddItem></AddItem>}
</div>

}
export default Showtable;
