import {useState} from 'react'
import AddItem from './additem';

const Showtable=(props)=>{
    const[isedit,setisedit]=useState(false)
    const[itemToedit,setitemToedit]=useState({})
    const edit=(item)=>{
        debugger
        //הצגת קומפוננטת עריכה
        setisedit(!isedit)
        setitemToedit(item)
    }

return <div className="container">
<table className="table">
<tr>
    <th>קוד</th>
    <th>שם</th>
    <th>מחיר</th>
    <th>תמונה</th>
    </tr>
{
    props.mylist.map(index=>(
        <tr>
            <td>{index.id}</td>
            <td>{index.name}</td>
            <td>{index.price}</td>
            <td><img src={index.img} width="50" height='50'></img></td>
             <td><button onClick={()=>props.ondelete(index.id)}>מחק</button></td>
            <td ><button onClick={()=>edit(index)}>ערוך</button> </td>
        </tr>
    ))
}

</table>
{/* itemtoedit={itemToedit}-שליחת פרמטר את מי נירצה לערוך */}
 {isedit && <AddItem itemtoedit={itemToedit} onedit={props.onedit}></AddItem>}
</div>
}
export default Showtable;
