import axios from 'axios'
import { useContext, useEffect, useState } from 'react';
import { useParams} from 'react-router-dom'

const Moredetails = () => {
  const [recipe,setrecipe]=useState()
  let params=useParams()
  
  useEffect(function(){
    axios.get(`http://localhost:1234/Recipes/getbyid/${params.id}`).then(l=>{
    setrecipe(l.data[0])})
  },[params])
  return (
    <div >
    <div class="jumbotron text-center" style={{width:500}}>
    <p><p>{recipe?.name}</p>  <p>{recipe?.description}</p>  <p>{recipe?.pic}</p>  <p></p>{recipe?.level}  {recipe?.duration}  {recipe?.type}  {recipe?.id}  </p>
    {recipe?.component.map(item=><span>{item.name}-{item.amount}</span>)}
    </div>
    </div>
  )
}

export default Moredetails
