import {Component} from 'react'

import AddFood from '../addFood/addFood';
export default class Food extends Component{
 //פונקציה  הנשלחת כפרמטר props צריכה להיות פונקציה אנונימית
    add=(f)=>{
       
        debugger
        let h=this.state
debugger
//הוספה למרך
this.setState({arr:this.state.arr.concat(f)})
this.setState({isShowAdd:!this.state.isShowAdd})
    }
    changIsShow(){
        this.setState({isShowAdd:!this.state.isShowAdd})
    }
    //פונקצית מחיקה המקבלת קוד למחיקה
    dell(id){
        debugger
       // הצבה בסטיט את כל האוביקטים שלא שווים לקוד
        this.setState({arr:this.state.arr.filter(s=>s.id!=id)})
    }
    constructor(){
        super()
        this.state={
           arr:[
              {id:1,name:"לחם",price:5} ,
              {id:2,name:"חלב",price:6} ,
              {id:3,name:"גבינה",price:3} ,
              {id:4,name:"לבן",price:1} ,
           ] ,
           isShowAdd:false
        }
    }
render(){
    return <>
    <table>
        <tr>
            <th>קוד</th>
            <th>שם</th>
            <th>מחיר</th>
        </tr>
        {
            this.state.arr.map(x=>(
                <tr>
                    <td>
                        {x.id}
                    </td>
                    <td>
                        {x.name}
                    </td>
                    <td>
                        {x.price}
                    </td>
                    <td>
                        <button onClick={()=>this.dell(x.id)}>מחק</button>
                    </td>
                </tr>
            ))
        }
    </table>
    <button onClick={()=>this.changIsShow()}>לחץ כאן כדי להוסיף</button>
    {/* משפט if */}
    {/* this.state.isShowAdd -במקרה שהסטיט שלך דלוק תציג את הוספה */}
   {this.state.isShowAdd && <AddFood onadd={this.add}></AddFood>}
    </>
}
}
