import axios from 'axios'
import { useContext, useEffect, useState } from 'react';
import { useParams} from 'react-router-dom'

const Moredetails = () => {
  const [recipe,setrecipe]=useState()
  let params=useParams()
  
  useEffect(function(){
    axios.get(`http://localhost:1234/Recipes/getbyid/${params.id}`).then(l=>{
    console.log(l);setrecipe(l.data[0])})
  },[])
  return (
    <p>{recipe.name}--{recipe.description}--{recipe.pic}--{recipe.level}--{recipe.duration}--{recipe.type}--{recipe.id}--{recipe.component}</p>
  )
}

export default Moredetails
