import axios from 'axios'
import React, { useEffect, useState } from 'react'

const AddRJ = () => {
  const [myrecipe, setmyrecipe]=useState([])
  const add=()=>{
    let y=myrecipe
    axios.put('http://localhost:1234/Recipes/addRecipe',myrecipe).then(l=>{setmyrecipe(l.data)},alert("Added successfully"))
  }
  
  return (
    <div>
      <input type='text' placeholder='Enter name' onChange={(e)=>setmyrecipe({name:e.target.value})}></input>
      <input type='text' placeholder='Enter description' onChange={(e)=>setmyrecipe({description:e.target.value})}></input>
      <input type='text' placeholder='Enter pic' onChange={(e)=>setmyrecipe({pic:e.target.value})}></input>
      <input type='text' placeholder='Enter level' onChange={(e)=>setmyrecipe({level:e.target.value})}></input>
      <input type='number' placeholder='Enter duration' onChange={(e)=>setmyrecipe({duration:e.target.value})}></input>
      <input type='text' placeholder='Enter type' onChange={(e)=>setmyrecipe({type:e.target.value})}></input>
      <input type='text' placeholder='Enter id' onChange={(e)=>setmyrecipe({id:e.target.value})}></input>
      <input type='' placeholder='Enter components' onChange={(e)=>setmyrecipe({component:e.target.value})}></input>
      <input type='button' value='Save' onClick={()=>add()}></input>
    </div>
  )
}

export default AddRJ
