import { Component } from 'react'
export default class Flowers extends Component {
    render() {
        //כיוון שרשימת הפרחים מוגדרת כ
        function del(f){
            //מציאת האינדקס של הפרח
            //כאשר נגדיר משתנה מקומי בתוך פונקציה יוגדר ע"י 
            let index=arrFlower.indexOf(f)
            arrFlower.splice(index,1)
            debugger
            // arrFlower.splice()
           // alert("תמחק אותי")
        //    הודעה לקונסול
        console.log("תמחק אותי"+f.name)

        }
        // הגדרת משתנה מסוג json
debugger
        const f1 = { id: 1, name: "חרצית", sesion: 'קיץ' }
        const arrFlower = [
            { id: 81, name: "חרצית", color: 'yellow' },
            { id: 42, name: "שושנה", color: 'red' },
            { id: 23, name: "חבצלת", color: 'red' },
            { id: 41, name: "כלנית", color: 'pink' }
        ]
        return <div>
                 <div>
           <table>
           {/* thead-בשביל הכותרות */}
           <thead>
               <tr>
                   <th>קוד</th>
                   <th>שם</th>
                   <th>צבע</th>
               </tr>
               </thead>
               {/* map-מעבר על כל הטבלה */}
               {
                  arrFlower.map(item=>(
                    // tbody-גוף הטבלה
                <tbody key={item.id}>
                      <tr >
                          <td>{item.id}</td>
                          <td>{item.name}</td>
                          <td>{item.color}</td>
                          {/* שליחה לפונקציה מקומית שמוחקת */}
                          <td><button onClick={()=>del(item)}>מחק</button></td>
                      </tr>
                      </tbody>)
                  ) 
               }
           </table>
        </div>
            {/* הצגת משתנה בודד */}
            {f1.id}*****{f1.name}*****{f1.sesion}
            {/* {arrFlower[0].id}*****{arrFlower[0].name}*****{arrFlower[0].color} */}
           
        </div>
    }

}
