import { Component } from "react"
export default class Counter extends Component {
   
    constructor() {
        //קריאה לבנאי של האבא
        super()
        // this.state = {}-הגדרת הסטיט שיכיל משתנים הקשורים לקומפוננטה ונירצה לשנות אותם בעתיד
        this.state = {
            mone: 1,
            name: 'sara',
            age: 7
        }
    }
    render() {
        function add() {
        this.setState({mone:this})
        }
        return <div >
***********************{this.state.mone}********************
            <button onClick={()=>add()}>
                +
</button>
        </div>

    }
}
