import react, {Component} from 'react'
import Son from './son';

export default class Father extends Component{
    add=()=>{
        this.setState({mone:this.state.mone+1})
    }

    myOnChange=(newMassege)=>{
        this.setState({massege:newMassege})
    }
    changeName=(myName)=>{
        this.setState({name:myName})
    }

    constructor(props){
      
        super(props)
        this.state={
            massege:" ",
            name:" ",
            mone:1
        }
    }
    render(){
        return<>
        <input type="text" onChange={(e)=>this.myOnChange(e.target.value)} ></input>

        <p> הבן שלי אומר </p>
        {this.state.name}
        <Son massege={this.state.massege} fun={this.changeName} mone={this.state.mone} funAdd={this.add}></Son>

        </>
    }
}
