import axios from 'axios'
import { useContext, useEffect, useState } from 'react';
import { useParams} from 'react-router-dom'


const Moredetails = () => {
  const [recipe,setrecipe]=useState()
  let params=useParams()
  
  
  useEffect(function(){
    axios.get(`http://localhost:1234/Recipes/getbyid/${params._id}`).then(l=>{
    setrecipe(l.data[0])})
  },[params])
  return (
    <div>
    <div><div className='container text-center' style={{fontSize:30, backgroundColor:'white', height:70, width:1260}}>{recipe?.name}</div></div>
    <div className='c' style={{backgroundImage:`url(${recipe?.pic})`,height:1000, width:1260,backgroundSize:"cover"}}>
    <p> <p>Descruption: {recipe?.description}</p><p>Level: {recipe?.level}</p>  <p>Duration: {recipe?.duration}</p>  <p>Type: {recipe?.type}</p>  <p>Id: {recipe?.id}</p><p>Components:</p>  
    <p>{recipe?.component.map(item=><span>*  {item.name} {item.amount}<br></br></span>)}</p></p>
    </div>
    </div>
  )
}

export default Moredetails
