import {useState,useEffect} from 'react'
import img1 from '../assets/img/pic1.jpg'
import img2 from '../assets/img/pic2.jpg'
import img3 from '../assets/img/pic3.jpg'
import img4 from '../assets/img/pic4.jpg'
import Item from './item';
import Showtable from './Showtable';
import AddItem from './additem';
// export default function Table(){

// }
//קומפוננטה מסוג פונקציה אנונימית
// state-משתנים הקשורים לקומפוננטה ונירצה לשנות אותם
//props-פרמטרים שהקומפוננטה מקבלת
const List=()=>{


    const [list,setlist]=useState([
        {id:1,name:"סופגניה ",price:120,img:img1},
        {id:2,name:"חנוכיה ",price:120,img:img2},
        {id:3,name:"סביבון",price:120,img:img3},
        {id:4,name:"נר ",price:120,img:img4},
        {id:5,name:"חנוכיה ",price:120,img:img2},
        {id:6,name:"סביבון",price:120,img:img3},
        {id:7,name:"נר ",price:120,img:img4},

    ])
//הוספה
// itemForm-אוביקט שנקבל מהטופס
    const add=(itemForm)=>{
    
    
// concat-פונקציה המוסיפה איבר לרשימה
setlist(p=>p.concat(itemForm))
    }
    //מחיקה
    const dell=(id)=>{
        //הצבה ברשימה המקורית רק את האוביקטים שלא שווים לקוד שהתקבל
        setlist(h=>h.filter(j=>j.id!=id))
    }
    //עדכון
    const update=(itemformToupdate)=>{
       //מחיקה
       dell(itemformToupdate.id)
       //הוספה
       add(itemformToupdate)
    }
return <div>
    {/* -mylist-פרמטר ששולח רשימה */}
    {/* ondelete-פונקציה שנשלחת כפרמטר */}
<Showtable mylist={list} ondelete={dell} > </Showtable>
>
<AddItem onAdd={add}></AddItem>




    {/* //מעבר על רשימה */}
    {/* item-רשומה נוכחית */}
    {/* שם הפרמטר={x.name} */}
   {
//        list.map(x=>(
// {/* <Item name={x.name} img={x.img} price={x.price}></Item> */}
//        ))
   }
</div>
}
//יצוא הקומפוננטה
export default List;
