import axios from 'axios'
import { useContext, useEffect, useState } from 'react';
import { useParams} from 'react-router-dom'

const Moredetails = () => {
  const [recipe,setrecipe]=useState()
  let params=useParams()
  
  
  useEffect(function(){
    axios.get(`http://localhost:1234/Recipes/getbyid/${params._id}`).then(l=>{
    setrecipe(l.data[0])})
  },[params])
  return (
    <div >
    <div>
    <p style={{marginLeft:500, color:'white'}}><div style={{fontSize:14}}>{recipe?.name}</div><div><img src={recipe?.pic} width="170" height="145"></img></div>  <p>{recipe?.description}</p>    <p>{recipe?.level}</p>  <p>{recipe?.duration}</p>  <p>{recipe?.type}</p>  <p>{recipe?.id}</p>  
    <p>{recipe?.component.map(item=><span>{item.name} {item.amount}<br></br></span>)}</p></p>
    </div>
    </div>
  )
}

export default Moredetails
