import axios from "axios"
import { useState , useEffect, useRef, useContext} from "react"
import { Link, Outlet } from "react-router-dom"
import RecipesContext from "../ourContext"

const LoginJ = () => {

  let myname=useRef(null)
  let mypass=useRef(null)
  const login=useContext(RecipesContext).login
  const setlogin=useContext(RecipesContext).setlogin
  const loginUser=()=>{
  axios.get(`http://localhost:1234/Users/getbyid/${document.getElementById("passwordI").value}/${document.getElementById("nameI").value}`).then(j=>setlogin(j.data[0]))
}
const check=()=>{
  
  let y=myname.cu
  let b=mypass.loginUser()
  if(y.value==null )
  {
    y.focus=true
    y.style.borderColor="red"
  }
  if(b.value==null )
  {
    b.focus=true
    b.style.borderColor="red"
  }
}
  return <div>
      <br></br>
      <div class="form-group">
      <div class="col-sm-10">          
      <input type='text' id="nameI" placeholder='Enter name'></input>
      </div>
      </div>
      <br></br>
      <div class="form-group">
      <div class="col-sm-10">          
      <input type='password' id="passwordI" placeholder='Enter password'></input> 
      </div>
      </div>
      <br></br>
      <div class="form-group">        
          <div class="col-sm-offset-0 col-sm-10">
          <button type='button' class="btn btn-danger" onClick={()=>loginUser()}>Login {login==null?<span className="badge">❌</span>:<span className="badge">✔</span>}</button>
          </div>
        </div>
     
      
    </div>
  
}

export default LoginJ
