import axios from 'axios'
import React, { useContext, useEffect, useRef, useState } from 'react'
import RecipesContext from '../ourContext'

const AddRJ = () => {
  let myN=useRef(null)
  let myD=useRef(null)
  let myP=useRef(null)
  let myL=useRef(null)
  let myDu=useRef(null)
  let myT=useRef(null)
  const listrecipe=useContext(RecipesContext).listrecipe
  const setlistrecipe=useContext(RecipesContext).setlistrecipe
  const login=useContext(RecipesContext).login
  const [list,setlist]=useState({Component:[]})
  const [name,setname]=useState({})
  const [amount, setamount]=useState({})
  // const add=()=>{
  //   let y=listrecipe
  //   axios.put('http://localhost:1234/Recipes/addRecipe',y).then(l=>{setlistrecipe(l.data)},alert("Added successfully"))
  // }
  const add=()=>{
    axios.put('http://localhost:1234/Recipes/addRecipe',list).then(l=>{
      let arr=[...listrecipe,list]
    })
  }
  const addCom=()=>
  {
       let newItem={name:name,amount:amount}
       setlist({...list,Component:[...(list.Component),newItem]})
       document.getElementById("name").value=""
       document.getElementById("amount").value=""
  }

  return (
    <div>
      {login==null?
      <div class="alert alert-warning">
        <strong>Sorry!</strong> You dont login to the site, you can't add recipe.
      </div>:
      <div class="container">
      <form class="form-horizontal">
        <div class="form-group">
          <div class="col-sm-10">
          <input type='text' class="form-control" placeholder='Enter name' onChange={(e)=>setlist({...list,name:e.target.value})}></input><br></br>
          <input type='text' class="form-control" placeholder='Enter description' onChange={(e)=>setlist({...list,description:e.target.value})}></input><br></br>        
          <input type='text' class="form-control" placeholder='Enter pic' onChange={(e)=>setlist({...list,pic:e.target.value})}></input><br></br>         
          <input type='text' class="form-control" placeholder='Enter level' onChange={(e)=>setlist({...list,level:e.target.value})}></input><br></br>          
          <input type='text' class="form-control" placeholder='Enter duration' onChange={(e)=>setlist({...list,duration:e.target.value})}></input><br></br>      
          <input type='text' class="form-control" placeholder='Enter type' onChange={(e)=>setlist({...list,type:e.target.value})}></input><br></br>       
          <input type='text' class="form-control" placeholder='Enter id' onChange={(e)=>setlist({...list,id:e.target.value})}></input>
          </div>
        </div>
        <div class="form-group">
          <div class="col-sm-4">          
          <input id='name' type='text' class="form-control" placeholder='Enter name' onChange={(e)=>setname({name:e.target.value})}></input>
          </div>
        </div>
        <div class="form-group">
          <div class="col-sm-4">          
          <input id='amount' type='text' class="form-control" placeholder='Enter amount' onChange={(e)=>setamount({amount:e.target.value})}></input>
          </div>
        </div>
        <div class="form-group">        
          <div class="col-sm-offset-0 col-sm-10">
            <button type="submit" class="btn btn-default" onClick={()=>addCom()}>SaveComponent</button>
          </div>
        </div>
        <div class="form-group">        
          <div class="col-sm-offset-5 col-sm-10">
            <button type="submit" class="btn btn-default" onClick={()=>add()}>Save</button>
          </div>
        </div>
        
      </form>
    </div>}
    </div>
  )
}

export default AddRJ
