import {useState,useEffect} from 'react'

export default function MyBody(props){
///כלים:
//const [שם,funcName]=useState([])
//הפונקציות יהיו פונקציות רגילות ולא אנונימיות
//לא יקרא לפונקציות עם this
    const [name,setname]=useState([])
    const [arr,setarr]=useState([
        
            {
                id:1,name:'aaa'
            },
            {
                id:2,name:'bbb'
            }
        
    ])
    const [a,seta]=useState([])
    const [b,setb]=useState([])
    // useEffect(function a(){
    //     alert ("שינו אותי")
    // },[name,a,b])
    //הצבת הפרמטרים בתוך משתנה 
   const h=props
  function   c1(val){
        setname(val.target.value)
}    
   function dell(id){
       setarr(arr.filter(k=>k.id))
   }
    return <p>
        {props.mycolor}
        {h.mycolor}
        {name}
        שלום וברכה
        <input onChange={(e)=> c1(e)} placeholder="enter name"></input>
        <input onChange={(e)=> seta(e.target.value)} placeholder="a"></input>
        <input onChange={(e)=> setb(e.target.value)} placeholder="b"></input>
        <Calc></Calc>
{arr.map(l=>(
<p>{l.id}----{l.name}---<button onClick={()=>dell(l.id)}></button></p>
))
    
}
    </p>
}
 function Calc(){
    return <p>{3+8}</p>
}
