import {useState,useEffect} from 'react'
import img1 from '../assets/img/pic1.jpg'
import img2 from '../assets/img/pic2.jpg'
import img3 from '../assets/img/pic3.jpg'
import img4 from '../assets/img/pic4.jpg'
import AddItem from './additem';
import Showtable from './showtable';
const List=()=>{
    const[isAdd,setisAdd]=useState(false)
    const [list,setlist]=useState([
        {id:1,name:"jjj",price:20,img:img1},
        {id:2,name:"xcddf",price:60,img:img2},
        {id:3,name:"sd",price:30,img:img3},
        {id:4,name:"as",price:40,img:img4}
    ])
//מחיקה
    const dell=(id)=>{
        setlist(h=>h.filter(j=>j.id!=id))
    }

//הוספה
const add=(itemForm)=>{
    setlist(p=>p.concat(itemForm))
}

const update=(itemformTOupdate)=>{
    dell(itemformTOupdate.id)
    add(itemformTOupdate)
}
return <div>
 <Showtable  mylist={list} ondelete={dell} onedit={update} ></Showtable>
 {/* <button onClick={()=>setisAdd(!isAdd)}>+</button>
 {isAdd && <AddItem onAdd={add}></AddItem>} */}
 </div>
}
export default List;
