import {Component} from 'react'
export default class Father extends Component{
    changeName(event){
        debugger
        // setState({'הסטיט לעריכה':value})
this.setState({myname:event.target.value})
    }
    constructor(props)
    {
        super(props)
        this.state={
            myname:''
        }
    }
    render(){
     
        return   <div>
            <label>הכנס שם</label>
        <input type="text" placeholder="" onChange={(event)=>this.changeName(event)}></input>
        <br></br>
        <label>השם שהכנסת</label>
        {this.state.myname}
        </div>
     
    }

}
