import axios from "axios"
import { useState , useEffect, useRef} from "react"
import { Link } from "react-router-dom"
const LoginJ = () => {
  let myname=useRef(null)
  let mypass=useRef(null)
const [myusers,setmyuser]=useState([])
const login=(id,name)=>{
  axios.get(`http://localhost:1234/Users/getbyid/${id}/${name}`).then(j=>setmyuser(j.data),[])
}
const check=()=>{
  let y=myname.current
  let b=mypass.current
  if(y.value=="" )
  {
    y.focus=true
    y.style.borderColor="red"
  }
  if(b.value=="" )
  {
    b.focus=true
    b.style.borderColor="red"
  }
}
  return <div>
      <input type='text' id="nameI" placeholder='Enter name' ref={myname}></input>
      <input type='password' id="passwordI" placeholder='Enter password' ref={mypass}></input> 
      <button type='submit' onClick={()=>login({id:document.getElementById('nameI').value,name:document.getElementById('passwordI').value},check())}>login</button>
      <p><Link type='butt' to='myaddr'>To add recipea</Link></p> 
    </div>
  
}

export default LoginJ
