import { Component } from 'react'
import Card from './card';

export default class BrachaCard extends Component {
    //חיבים לקרוא לפרמטרים props
    constructor(props) {
        debugger
        super(props)
    }
    render() {
        const mycss = { color: "gray", fontSize: '50px' }
        //יש אפשרות ליצור תנאי על המשתנים או על הפרמטרים
        return <>{this.props.min == 'ז' ?
            <div style={{ backgroundColor: "blue", width: '100%', height: '150px' }}>
               <Card min={this.props.min} wish={this.props.wish}></Card>
            </div>:
             <div style={{ backgroundColor: "pink", width: '100%', height: '150px' }}>
              <Card></Card>
         </div>
        }
        </>
    }
}
